import React from 'react';
import { gql, useQuery } from '@apollo/client';
import { Col, Spinner, Alert, Image } from 'react-bootstrap';
import {
	useMessageDispatch,
	useMessageState,
} from '../../context/messageContext';

const GET_USERS = gql`
	query getUsers {
		getUsers {
			username
			createdAt
			imageUrl
			latestMessage {
				from
				to
				content
			}
		}
	}
`;

const Users = ({ setSelectedUser }) => {
	const dispatch = useMessageDispatch();
	const { users } = useMessageState();
	const { loading } = useQuery(GET_USERS, {
		onCompleted: data => dispatch({ type: 'SET_USER', payload: data.getUsers }),
		onError: err => console.log(err),
	});
	let usersMarkup;
	if (!users && loading) {
		usersMarkup = <Spinner animation='grow' />;
	} else if (users.length === 0) {
		usersMarkup = (
			<Alert variant='info'>No users have joined this platform yet</Alert>
		);
	} else if (users.length > 0) {
		usersMarkup = users.map(user => (
			<div
				className='d-flex p-3'
				key={user._id}
				onClick={() => setSelectedUser(user.username)}
			>
				<Image
					src='https://source.unsplash.com/user/erondu/1600x900'
					roundedCircle
					className='mr-2'
					style={{ width: 50, height: 50, objectFit: 'cover' }}
				/>
				<div>
					<p className='text-success'>{user.username}</p>
					<p className='font-weight-light'>
						{user.latestMessage
							? user.latestMessage.content
							: 'You are connected'}
					</p>
				</div>
			</div>
		));
	}
	return (
		<Col
			className=' p-0'
			xs={4}
			style={{
				backgroundColor: ' #8EC5FC',
				opacity: '85%',

				backgroundImage:
					'linear-gradient(62deg, #8EC5FC 0%, #ffffff 49%, #E0C3FC 98%)',
			}}
		>
			{usersMarkup}
		</Col>
	);
};

export default Users;
