import React, { useEffect } from 'react';
import {
	Row,
	Col,
	Button,
	Navbar,
	Nav,
	Spinner,
	Alert,
	Image,
} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useAuthDispatch } from '../../context/authContext';
import { gql, useQuery, useLazyQuery } from '@apollo/client';
import { Fragment } from 'react';
import { useState } from 'react';

const GET_USERS = gql`
	query getUsers {
		getUsers {
			username
			createdAt
			imageUrl
			latestMessage {
				from
				to
				content
			}
		}
	}
`;
const GET_MESSAGES = gql`
	query getMessages($from: String!) {
		getMessages(from: $from) {
			_id
			content
			createdAt
			to
			from
		}
	}
`;
const Home = ({ history }) => {
	const dispatch = useAuthDispatch();
	const [selectedUser, setSelectedUser] = useState(null);
	const [getMessages, { loading: messagesLoading, data: messagesData }] =
		useLazyQuery(GET_MESSAGES);
	useEffect(() => {
		effect;
		if (selectedUser) {
		}
	}, [input]);

	const logout = () => {
		dispatch({ type: 'LOGOUT' });
		history.push('/login');
	};
	const { loading, data, error } = useQuery(GET_USERS);

	if (data) console.log(data);

	let usersMarkup;
	if (!data && loading) {
		usersMarkup = <Spinner animation='grow' />;
	} else if (data.getUsers.length === 0) {
		usersMarkup = (
			<Alert variant='info'>No users have joined this platform yet</Alert>
		);
	} else if (data.getUsers.length > 0) {
		usersMarkup = data.getUsers.map(user => (
			<div
				className='d-flex p-3'
				key={user._id}
				onClick={() => setSelectedUser(user.username)}
			>
				<Image
					src='https://source.unsplash.com/user/erondu/1600x900'
					roundedCircle
					className='mr-2'
					style={{ width: 50, height: 50, objectFit: 'cover' }}
				/>
				<div>
					<p className='text-success'>{user.username}</p>
					<p className='font-weight-light'>
						{user.latestMessage
							? user.latestMessage.content
							: 'You are connected'}
					</p>
				</div>
			</div>
		));
	}
	return (
		// <Row className='bg-white justify-content-around'>
		// 	<Link to='/login'>
		// 		<Button variant='link'>Login</Button>
		// 	</Link>
		// 	<Link to='/register'>
		// 		<Button variant='link'>Register</Button>
		// 	</Link>
		// 	<Button variant='link' onClick={logout}>
		// 		Logout
		// 	</Button>
		// </Row>
		<Fragment>
			<Navbar bg='light' expand='lg' className='mb-3'>
				<Navbar.Brand href='#home'>MaChat</Navbar.Brand>
				<Navbar.Toggle aria-controls='basic-navbar-nav' />

				<Navbar.Collapse id='basic-navbar-nav' className='justify-content-end'>
					<Nav>
						<Nav.Link onClick={logout}>Logout</Nav.Link>
					</Nav>
					{/* <Form inline>
					<FormControl type='text' placeholder='Search' className='mr-sm-2' />
					<Button variant='outline-success'>Search</Button>
				</Form> */}
				</Navbar.Collapse>
			</Navbar>
			<Row className=' m-0'>
				<Col
					className=' p-0'
					xs={4}
					style={{
						backgroundColor: ' #8EC5FC',
						opacity: '85%',

						backgroundImage:
							'linear-gradient(62deg, #8EC5FC 0%, #ffffff 49%, #E0C3FC 98%)',
					}}
				>
					{usersMarkup}
				</Col>
				{}
				<Col xs={8}>
					<p>Message</p>
				</Col>
			</Row>
		</Fragment>
	);
};

export default Home;
